'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:engine:components:text' );
var h = require( 'virtual-dom/h' );


// TEXT //

/**
* Renders tick text as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @param {*} d - tick value
* @returns {VTree} virtual tree
*/
function text( ctx, d ) {
	var opts;
	var txt;
	var fmt;
	var dir;
	var x;
	var y;

	fmt = ctx.tickFormat();

	dir = ctx.tickDir();
	debug( 'Tick direction: %d.', dir );

	x = ctx.xAttr();
	y = ctx.yAttr();

	opts = {
		'namespace': 'http://www.w3.org/2000/svg',
		'attributes': {
			'fill': '#000',
			'dy': ctx.dy()
		}
	};
	opts.attributes[ x ] = dir * ctx.spacing();
	opts.attributes[ y ] = 0.5;

	txt = fmt( d );
	debug( 'Tick text: %s.', txt );

	debug( 'Generating a virtual DOM tree with the following configuration: %s.', JSON.stringify( opts ) );

	return h( 'text', opts, [ txt ] );
} // end FUNCTION text()


// EXPORTS //

module.exports = text;
