'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:engine:components:text' );
var h = require( 'virtual-dom/h' );
var copy = require( '@stdlib/utils/copy' );


// VARIABLES //

var ELEMENT = 'text';
var PROPS = {
	'namespace': 'http://www.w3.org/2000/svg',
	'attributes': {
		'fill': '#000',
		'dy': null
	}
};


// RENDER //

/**
* Renders tick text.
*
* @private
* @param {Object} ctx - context
* @param {*} d - tick value
* @returns {VTree} virtual tree
*/
function render( ctx, d ) {
	var props;
	var txt;
	var fmt;
	var dir;
	var x;
	var y;

	props = copy( PROPS );

	fmt = ctx.tickFormat();
	dir = ctx.tickDir();
	debug( 'Tick direction: %d.', dir );

	x = ctx.xAttr();
	y = ctx.yAttr();

	props.attributes.dy = ctx.dy();
	props.attributes[ x ] = dir * ctx.spacing();
	props.attributes[ y ] = 0.5;

	txt = fmt( d );
	debug( 'Tick text: %s.', txt );

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( props ) );

	return h( ELEMENT, props, txt );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
