'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:engine:components:tick' );
var h = require( 'virtual-dom/h' );
var line = require( './line.js' );
var text = require( './text.js' );


// TICK //

/**
* Renders an axis tick as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @param {*} d - tick value
* @returns {VTree} virtual tree
*/
function tick( ctx, d ) {
	var transform;
	var children;
	var opts;

	transform = ctx.transform();

	opts = {
		'namespace': 'http://www.w3.org/2000/svg',
		'property': 'axis.tick',
		'className': 'tick',
		'attributes': {
			'opacity': 1,
			'transform': transform( d )
		}
	};

	debug( 'Creating tick line...' );
	debug( 'Creating tick text...' );
	children = [
		line( ctx ),
		text( ctx, d )
	];

	debug( 'Generating a virtual DOM tree with the following configuration: %s.', JSON.stringify( opts ) );

	return h( 'g', opts, children );
} // end FUNCTION tick()


// EXPORTS //

module.exports = tick;
