'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:engine:components:tick' );
var h = require( 'virtual-dom/h' );
var line = require( './line.js' );
var text = require( './text.js' );


// VARIABLES //

var ELEMENT = 'g';
var PROPS = {
	'namespace': 'http://www.w3.org/2000/svg',
	'property': 'axis.tick',
	'className': 'tick',
	'attributes': {
		'opacity': 1,
		'transform': null
	}
};
var CHILDREN = new Array( 2 );


// TICK //

/**
* Renders an axis tick as a virtual DOM tree.
*
* @private
* @param {Object} ctx - context
* @param {*} d - tick value
* @returns {VTree} virtual tree
*/
function tick( ctx, d ) {
	PROPS.attributes.transform = ctx.transform()( d );

	debug( 'Creating tick line...' );
	CHILDREN[ 0 ] = line( ctx );

	debug( 'Creating tick text...' );
	CHILDREN[ 1 ] = text( ctx, d );

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( PROPS ) );

	return h( ELEMENT, PROPS, CHILDREN );
} // end FUNCTION tick()


// EXPORTS //

module.exports = tick;
