'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:engine:components:tick' );
var h = require( 'virtual-dom/h' );
var line = require( './line.js' );
var text = require( './text.js' );


// VARIABLES //

var ELEMENT = 'g';


// RENDER //

/**
* Renders an axis tick.
*
* @private
* @param {Object} ctx - context
* @param {*} d - tick value
* @returns {VTree} virtual tree
*/
function render( ctx, d ) {
	var children;
	var props;

	props = {
		'namespace': 'http://www.w3.org/2000/svg',
		'property': 'axis.tick',
		'className': 'tick',
		'attributes': {
			'opacity': 1,
			'transform': ctx.transform()( d )
		}
	};
	children = new Array( 2 );

	debug( 'Rendering a tick line...' );
	children[ 0 ] = line( ctx );

	debug( 'Rendering tick text...' );
	children[ 1 ] = text( ctx, d );

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( props ) );

	return h( ELEMENT, props, children );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
