'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:engine:components:ticks' );
var tick = require( './tick.js' );


// RENDER //

/**
* Renders axis ticks.
*
* @private
* @param {Object} ctx - context
* @returns {Array<VTree>} array of virtual DOM nodes
*/
function render( ctx ) {
	var values;
	var out;
	var i;

	values = ctx.state.ticks;
	debug( 'Tick values: %s.', JSON.stringify( values ) );

	debug( 'Rendering ticks...' );
	out = new Array( values.length );
	for ( i = 0; i < values.length; i++ ) {
		debug( 'Render tick %d with value %s...', i, values[i] );
		out[ i ] = tick( ctx, values[i] );
	}
	debug( 'Finished rendering ticks.' );
	return out;
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
