'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:engine:components:ticks' );
var tick = require( './tick.js' );


// TICKS //

/**
* Renders axis ticks as an array of virtual DOM nodes.
*
* @private
* @param {Object} ctx - context
* @returns {Array<VTree>} array of virtual DOM nodes
*/
function ticks( ctx ) {
	var values;
	var out;
	var i;

	values = ctx.ticks();
	debug( 'Tick values: %s.', JSON.stringify( values ) );

	debug( 'Creating ticks...' );
	out = new Array( values.length );
	for ( i = 0; i < values.length; i++ ) {
		debug( 'Creating tick %d with value %s...', i, values[i] );
		out[ i ] = tick( ctx, values[i] );
	}
	debug( 'Finished creating ticks.' );
	return out;
} // end FUNCTION ticks()


// EXPORTS //

module.exports = ticks;
