'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:engine' );
var EventEmitter = require( 'events' ).EventEmitter;
var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var copy = require( '@stdlib/utils/copy' );
var validate = require( './validate.js' );
var defaults = require( './defaults.json' );


// AXIS ENGINE //

/**
* Axis engine constructor.
*
* @constructor
* @param {Object} state - engine state
* @param {Options} [options] - engine options
* @throws {TypeError} must provide valid options
* @returns {AxisEngine} engine instance
*/
function AxisEngine( state, options ) {
	var self;
	var opts;
	var err;
	if ( !( this instanceof AxisEngine ) ) {
		if ( arguments.length > 1 ) {
			return new AxisEngine( state, options );
		}
		return new AxisEngine( state );
	}
	self = this;
	opts = copy( defaults );
	if ( arguments.length > 1 ) {
		err = validate( opts, options );
		if ( err ) {
			throw err;
		}
	}
	debug( 'Creating an axis engine with the following configuration: %s.', JSON.stringify( opts ) );
	EventEmitter.call( this );

	// Public, read-only properties...
	setReadOnly( this, 'state', state );

	// "Private" properties...
	Object.defineProperty( this, '_opts', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': opts
	});

	// Bind a listener to update on state changes:
	this.state.on( 'change', onChange );

	return this;

	function onChange() {
		debug( 'Received a change event.' );
		self.render();
	}
} // end FUNCTION AxisEngine()

/*
* Create a prototype which inherits from the parent prototype.
*/
AxisEngine.prototype = Object.create( EventEmitter.prototype );

/*
* Set the constructor.
*/
AxisEngine.prototype.constructor = AxisEngine;

/**
* Computes the tick spacing.
*
* @memberof AxisEngine.prototype
* @type {Function}
* @name spacing
* @returns {number} tick spacing
*/
setReadOnly( AxisEngine.prototype, 'spacing', require( './methods/spacing.js' ) );

/**
* Returns a function for translating ticks.
*
* @memberof AxisEngine.prototype
* @type {Function}
* @name transform
* @returns {Function} transform
*/
setReadOnly( AxisEngine.prototype, 'translateTicks', require( './methods/translate-ticks.js' ) );

/**
* Returns the tick values.
*
* @memberof AxisEngine.prototype
* @type {Function}
* @name ticks
* @returns {Array} ticks
*/
setReadOnly( AxisEngine.prototype, 'ticks', require( './methods/ticks.js' ) );

/**
* Returns the tick format.
*
* @memberof AxisEngine.prototype
* @type {Function}
* @name tickFormat
* @returns {Function} tick format
*/
setReadOnly( AxisEngine.prototype, 'tickFormat', require( './methods/tickformat.js' ) );

/**
* Returns a function for computing tick positions.
*
* @memberof AxisEngine.prototype
* @type {Function}
* @name tickPos
* @returns {Function} function to compute tick positions
*/
setReadOnly( AxisEngine.prototype, 'tickPos', require( './methods/tickpos.js' ) );

/**
* Returns the tick direction.
*
* @memberof AxisEngine.prototype
* @type {Function}
* @name tickDir
* @returns {number} tick direction
*/
setReadOnly( AxisEngine.prototype, 'tickDir', require( './methods/tickdir.js' ) );

/**
* Returns the vertical shift for positioning tick text.
*
* @memberof AxisEngine.prototype
* @type {Function}
* @name dy
* @returns {string} vertical shift
*/
setReadOnly( AxisEngine.prototype, 'dy', require( './methods/dy.js' ) );

/**
* Returns the text anchor value for text positioning.
*
* @memberof AxisEngine.prototype
* @type {Function}
* @name textAnchor
* @returns {string} anchor value
*/
setReadOnly( AxisEngine.prototype, 'textAnchor', require( './methods/textanchor.js' ) );

/**
* Returns the "x" attribute for tick positioning.
*
* @memberof AxisEngine.prototype
* @type {Function}
* @name xAttr
* @returns {string} attribute name
*/
setReadOnly( AxisEngine.prototype, 'xAttr', require( './methods/xattr.js' ) );

/**
* Returns the "y" attribute for tick positioning.
*
* @memberof AxisEngine.prototype
* @type {Function}
* @name yAttr
* @returns {string} attribute name
*/
setReadOnly( AxisEngine.prototype, 'yAttr', require( './methods/yattr.js' ) );

/**
* Renders an axis.
*
* @memberof AxisEngine.prototype
* @type {Function}
* @name render
* @returns {VTree} virtual tree
*/
setReadOnly( AxisEngine.prototype, 'render', require( './methods/render.js' ) );


// EXPORTS //

module.exports = AxisEngine;
