'use strict';

// MODULES //

var axis = require( './../components/axis.js' );


// RENDER //

/**
* Renders an axis as a virtual DOM tree.
*
* @private
* @returns {VTree} virtual tree
*/
function render() {
	/* jshint validthis: true */ // TODO: eslint
	var vtree;

	// Generate the axis:
	vtree = axis( this );

	// Announce that a new tree has been rendered:
	this.emit( 'render', vtree );

	return vtree;
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
