'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:engine:render' );
var axis = require( './../components' );


// RENDER //

/**
* Renders an axis as a virtual DOM tree.
*
* @private
* @returns {VTree} virtual tree
*/
function render() {
	/* jshint validthis: true */ // TODO: eslint
	var vtree;

	debug( 'Rendering an axis...' );

	// Generate the axis:
	vtree = axis( this );

	// Announce that a new tree has been rendered:
	this.emit( 'render', vtree );

	return vtree;
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
