'use strict';

// MODULES //

var format = require( 'd3-format' ).format;
var isString = require( '@stdlib/utils/is-string' ).isPrimitive;
var isNull = require( '@stdlib/utils/is-null' );
var identity = require( '@stdlib/utils/identity-function' );


// TICK FORMAT //

/**
* Returns the tick format function.
*
* @private
* @returns {Function} tick format
*/
function tickFormat() {
	/* jshint validthis: true */
	var scale;
	var fmt;
	fmt = this.state.tickFormat;
	if ( isString( fmt ) ) {
		return format( fmt );
	}
	if ( isNull( fmt ) ) {
		scale = this.state.scale;
		if ( scale.tickFormat ) {
			return scale.tickFormat( this.state.numTicks, this.state.tickFormat );
		}
		return identity;
	}
	return fmt;
} // end FUNCTION tickFormat()


// EXPORTS //

module.exports = tickFormat;
