'use strict';

// MODULES //

var center = require( './../utils/center.js' );


// POSITION //

/**
* Returns a function for positioning ticks.
*
* @private
* @returns {Function} position function
*/
function tickPos() {
	/* jshint validthis: true */ // TODO: eslint
	var scale = this.state.scale;
	scale = scale.copy();
	if ( scale.bandwidth ) {
		return center( scale );
	}
	return scale;
} // end FUNCTION tickPos()


// EXPORTS //

module.exports = tickPos;
