'use strict';

/**
* Returns tick values.
*
* @private
* @returns {Array} tick values
*/
function ticks() {
	/* jshint validthis: true */ // TODO: eslint
	var scale;
	var vals;
	vals = this.state.ticks;
	if ( vals === null ) {
		scale = this.state.scale;
		if ( scale.ticks ) {
			return scale.ticks( this.state.numTicks, this.state.tickFormat );
		}
		return scale.domain();
	}
	return vals;
} // end FUNCTION ticks()


// EXPORTS //

module.exports = ticks;
