'use strict';

// MODULES //

var translateX = require( './../utils/translate-x.js' );
var translateY = require( './../utils/translate-y.js' );


// TRANSFORM //

/**
* Returns a tick transform function.
*
* @private
* @returns {Function} transform
*/
function transform() {
	/*jshint validthis: true */ // TODO: eslint
	var orient = this.state.orientation;
	if ( orient === 'top' || orient === 'bottom' ) {
		return translateX( this.state.scale );
	}
	return translateY( this.state.scale );
} // end FUNCTION transform()


// EXPORTS //

module.exports = transform;
