'use strict';

/**
* Returns the "x" attribute for tick positioning.
*
* @private
* @returns {string} attribute
*/
function xAttr() {
	/* jshint validthis:true */ // TODO: eslint
	var orient = this.state.orientation;
	if ( orient === 'left' || orient === 'right' ) {
		return 'x';
	}
	return 'y';
} // end FUNCTION xAttr()


// EXPORTS //

module.exports = xAttr;
