'use strict';

/**
* Returns the "y" attribute for tick positioning.
*
* @private
* @returns {string} attribute
*/
function yAttr() {
	/* jshint validthis:true */ // TODO: eslint
	var orient = this.state.orientation;
	if ( orient === 'left' || orient === 'right' ) {
		return 'y';
	}
	return 'x';
} // end FUNCTION yAttr()


// EXPORTS //

module.exports = yAttr;
