'use strict';

/**
* Returns vertical shift for aligning tick text.
*
* @private
* @returns {string} text shift
*/
function get() {
	/* jshint validthis:true */ // TODO: eslint
	var orient = this.state.orientation;
	if ( orient === 'top' ) {
		return '0em';
	}
	if ( orient === 'bottom' ) {
		return '.71em';
	}
	return '.32em';
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
