'use strict';

/**
* Returns the label `x` coordinate.
*
* @private
* @returns {number} `x` coordinate
*/
function get() {
	/* jshint validthis:true */ // TODO: eslint
	var orient = this.state.orientation;
	if ( orient === 'left' || orient === 'right' ) {
		return -this.state.scale.range()[0] / 2;
	}
	return this.state.scale.range()[1] / 2;
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
