'use strict';

/**
* Returns the text anchor value for text positioning.
*
* @private
* @returns {string} text anchor value
*/
function get() {
	/* jshint validthis:true */ // TODO: eslint
	var orient = this.state.orientation;
	if ( orient === 'left' ) {
		return 'end';
	}
	if ( orient === 'right' ) {
		return 'start';
	}
	return 'middle';
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
