'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:engine:center' );


// CENTER //

/**
* Returns a function to center a tick.
*
* @private
* @param {Function} scale - scale function
* @returns {Function} function to center a tick
*/
function center( scale ) {
	var width = scale.bandwidth() / 2;

	/**
	* Returns a centered tick position.
	*
	* @private
	* @param {*} d - datum
	* @returns {number} tick position
	*/
	return function center( d ) {
		var pos = scale( d ) + width;
		debug( 'Value: %s => Pixel: %d', d, pos );
		return pos;
	};
} // end FUNCTION center()


// EXPORTS //

module.exports = center;
