'use strict';

/**
* Returns the tick direction.
*
* @private
* @returns {number} tick direction
*/
function tickDir() {
	/* jshint validthis:true */ // TODO: eslint
	if (
		this._orientation === 'top' ||
		this._orientation === 'left'
	) {
		return -1;
	}
	return 1;
} // end FUNCTION tickDir()


// EXPORTS //

module.exports = tickDir;
