'use strict';

// MODULES //

var debug = require( 'debug' )( 'axis:set:engine' );
var isString = require( '@stdlib/utils/is-string' ).isPrimitive;
var createEngine = require( './../../engines' );
var events = require( './../../events' );


// VARIABLES //

var CHANGE_EVENT = events( 'engine' );


// SET //

/**
* Sets the axis engine.
*
* @private
* @param {string} engine - axis engine
* @throws {TypeError} must be a string primitive
*/
function set( engine ) {
	/*jshint validthis: true */ // TODO: eslint

	// TODO: verify valid engine
	if ( !isString( engine ) ) {
		throw new TypeError( 'invalid value. `engine` must be a string primitive. Value: `' + engine + '`.' );
	}
	debug( 'Current value: %s.', this._engine );

	this._engine = engine;
	debug( 'New value: %s.', this._engine );

	// TODO: engine options?
	this.__engine__ = createEngine( this, {
		'engine': this._engine
	});

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
