'use strict';

/**
* Horizontally translates a tick.
*
* @private
* @param {Function} scale - scale function
* @param {*} d - datum
* @returns {string} transform
*/
function translateX( scale, d ) {
	return 'translate('+scale( d )+',0)';
} // end FUNCTION translateX()


// EXPORTS //

module.exports = translateX;
