function translateX( scale0, scale1, d ) {
	var right;
	var x;
	x = scale0( d );
	if ( isfinite( x ) ) {
		right = x;
	} else {
		right = scale1( d );
	}
	return 'translate('+right+',0)';
} // end FUNCTION translateX()


