'use strict';

/**
* Vertically translates a tick.
*
* @private
* @param {Function} scale - scale function
* @param {*} d - datum
* @returns {string} transform
*/
function translateY( scale, d ) {
	return 'translate(0,'+scale( d )+')';
} // end FUNCTION translateY()


// EXPORTS //

module.exports = translateY;
