'use strict';

// MODULES //

var isNull = require( '@stdlib/utils/is-null' );
var isArray = require( '@stdlib/utils/is-array' );


/**
* Validates `ticks`.
*
* @private
* @param {*} v - value to test
* @returns {(Error|null)} error object or null
*/
function test( v ) {
	if (
		!isNull( v ) &&
		!isArray( v )
	) {
		return new TypeError( 'invalid value. `ticks` must be either null or an array. Value: `' + v + '.`' );
	}
	return null;
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
