'use strict';

// MODULES //

var debug = require( 'debug' )( 'base-2d:engine:components:base' );
var h = require( 'virtual-dom/h' );
var defs = require ('./defs.js' );
var graph = require( './graph.js' );
var meta = require( './meta.js' );


// VARIABLES //

var ELEMENT = 'svg';


// RENDER //

/**
* Renders a plot base.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	var children;
	var props;

	props = {
		'namespace': 'http://www.w3.org/2000/svg',
		'property': 'canvas',
		'className': 'canvas',
		'attributes': {
			'width': ctx.state.width,
			'height': ctx.state.height
		}
	};

	children = new Array( 3 );

	debug( 'Rendering definitions...' );
	children[ 0 ] = defs( ctx );

	debug( 'Rendering graph...' );
	children[ 1 ] = graph( ctx );

	debug( 'Rendering meta elements...' );
	children[ 2 ] = meta( ctx );

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( props ) );

	return h( ELEMENT, props, children );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
