'use strict';

// MODULES //

var debug = require( 'debug' )( 'base-2d:engine:components:meta' );
var h = require( 'virtual-dom/h' );
var title = require( './title.js' );


// ELEMENTS //

var ELEMENT = 'g';


// RENDER //

/**
* Renders an SVG element containing meta information.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	var children;
	var props;

	props = {
		'namespace': 'http://www.w3.org/2000/svg',
		'property': 'meta',
		'className': 'meta',
		'attributes': {
			'transform': 'translate(0,0)'
		}
	};

	debug( 'Rendering title...' );
	children = [
		title( ctx )
	];

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( props ) );

	return h( ELEMENT, props, children );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
