'use strict';

// MODULES //

var debug = require( 'debug' )( 'base-2d:engine:components:title' );
var h = require( 'virtual-dom/h' );


// VARIABLES //

var ELEMENT = 'text';


// RENDER //

/**
* Renders a title.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	var title;
	var props;

	props = {
		'namespace': 'http://www.w3.org/2000/svg',
		'property': 'title',
		'className': 'title noselect',
		'attributes': {
			'x': 0,
			'y': 0
		}
	};

	title = ctx.state.title;
	debug( 'Title: %s.', title );

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( props ) );

	return h( ELEMENT, props, title );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
