'use strict';

// MODULES //

var debug = require( 'debug' )( 'base-2d:engine:components:yaxis' );


// RENDER //

/**
* Renders a y-axis.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	var axis;

	debug( 'Generating a virtual DOM tree...' );
	axis = ctx.state.yAxis.render();

	// Update the class names:
	axis.properties.className += ' y';

	// Translate the axis into position:
	axis.properties.attributes.transform = ctx.yAxisTransform;

	return axis;
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
