'use strict';

// MODULES //

var debug = require( 'debug' )( 'base-2d:engine:render' );
var base = require( './../components/base.js' );


// RENDER //

/**
* Renders a plot base.
*
* @private
* @returns {VTree} virtual tree
*/
function render() {
	/* jshint validthis: true */ // TODO: eslint
	var vtree;

	debug( 'Rendering...' );

	// Generate the plot:
	vtree = base( this );

	// Announce that a new tree has been rendered:
	this.emit( 'render', vtree );

	return vtree;
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
