'use strict';

/**
* Returns a y-axis translation transform.
*
* @private
* @returns {string} transform
*/
function get() {
	/*jshint validthis: true */ // TODO: eslint
	var orient = this.state.yAxisOrient;
	if ( orient === 'left' ) {
		return 'translate(0,0)';
	}
	return 'translate('+this.graphWidth+',0)';
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
