'use strict';

// MODULES //

var Canvas = require( '@stdlib/plot/canvas-2d' );


// GET //

/**
* Returns a canvas.
*
* @private
* @returns {Canvas} canvas instance
*/
function get() {
	/* jshint validthis:true */ // TODO: eslint
	var canvas = new Canvas({
		'engine': this.engine,
		'width': this.width,
		'height': this.height,
		'paddingLeft': this.paddingLeft,
		'paddingRight': this.paddingRight,
		'paddingTop': this.paddingTop,
		'paddingBottom': this.paddingBottom,
		'xScale': this.xScale,
		'yScale': this.yScale,
		'xTickFormat': this.xTickFormat,
		'yTickFormat': this.yTickFormat,
		'xNumTicks': this.xNumTicks,
		'yNumTicks': this.yNumTicks,
		'xAxisOrient': this.xAxisOrient,
		'yAxisOrient': this.yAxisOrient,
		'title': this.title,
		'xLabel': this.xLabel,
		'yLabel': this.yLabel
	});
	return canvas;
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
