'use strict';

// MODULES //

var debug = require( 'debug' )( 'base-2d:set:labels' );
var events = require( './../../events' );
var isValid = require( './../../validators/labels.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'labels' );


// SET //

/**
* Sets the plot data labels.
*
* @private
* @param {(StringArray|EmptyArray)} labels - data labels
* @throws {TypeError} must be either an array of strings or an empty array
*/
function set( labels ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( labels );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %s.', JSON.stringify( this._labels ) );

	this._labels = labels.slice();
	debug( 'New Value: %s.', JSON.stringify( this._labels ) );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
