'use strict';

// MODULES //

var debug = require( 'debug' )( 'base-2d:set:title' );
var events = require( './../../events' );
var isValid = require( './../../validators/title.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'title' );


// SET //

/**
* Sets the title.
*
* @private
* @param {string} str - title
* @throws {TypeError} must be a string primitive
*/
function set( str ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( str );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %s.', this._title );

	this._title = str;
	debug( 'New value: %s.', this._title );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
