'use strict';

// MODULES //

var Axis = require( '@stdlib/plot/components/svg/axis' );


// GET //

/**
* Returns an x-axis.
*
* @private
* @returns {Axis} axis instance
*/
function get() {
	/* jshint validthis:true */ // TODO: eslint
	var axis = new Axis({
		'scale': this.xScale,
		'label': this.xLabel,
		'tickFormat': this.xTickFormat,
		'numTicks': this.xNumTicks,
		'orientation': this.xAxisOrient,
	});
	return axis;
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
