'use strict';

// MODULES //

var debug = require( 'debug' )( 'base-2d:set:x' );
var events = require( './../../events' );
var isValid = require( './../../validators/x.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'x' );


// SET //

/**
* Sets the plot `x` values.
*
* @private
* @param {(Array|TypedArray)} x - x values
* @throws {TypeError} must be an array or typed array
*/
function set( x ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( x );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %s.', JSON.stringify( this._xData ) );

	// TODO: copy?
	this._xData = x;
	debug( 'New Value: %s.', JSON.stringify( this._xData ) );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
