'use strict';

// MODULES //

var isEmptyArray = require( '@stdlib/utils/is-empty-array' );
var isStringArray = require( '@stdlib/utils/is-string' ).isPrimitiveStringArray;


/**
* Validates `labels`.
*
* @private
* @param {*} v - value to test
* @returns {(Error|null)} error object or null
*/
function test( v ) {
	if (
		!isEmptyArray( v ) &&
		!isStringArray( v )
	) {
		return new TypeError( 'invalid value. `labels` must be either a string or empty array. Value: `' + v + '.`' );
	}
	return null;
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
