'use strict';

// MODULES //

var isArray = require( '@stdlib/utils/is-array' );
var isTypedArray = require( '@stdlib/utils/is-typed-array' );


/**
* Validates `x`.
*
* @private
* @param {*} x - value to test
* @returns {(Error|null)} error object or null
*/
function test( x ) {
	if (
		!isArray( x ) &&
		!isTypedArray( x )
	) {
		return new TypeError( 'invalid value. `x` must be either an array or typed array. Value: `' + x + '.`' );
	}
	return null;
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
