'use strict';

// MODULES //

var isArray = require( '@stdlib/utils/is-array' );
var isTypedArray = require( '@stdlib/utils/is-typed-array' );


/**
* Validates `y`.
*
* @private
* @param {*} y - value to test
* @returns {(Error|null)} error object or null
*/
function test( y ) {
	if (
		!isArray( y ) &&
		!isTypedArray( y )
	) {
		return new TypeError( 'invalid value. `y` must be either an array or typed array. Value: `' + y + '.`' );
	}
	return null;
} // end FUNCTION test()


// EXPORTS //

module.exports = test;
