'use strict';

// MODULES //

var debug = require( 'debug' )( 'canvas-2d:engine:components:graph' );
var h = require( 'virtual-dom/h' );
var bkgd = require( './background.js' );
var marks = require( './marks.js' );
var xAxis = require( './xaxis.js' );
var yAxis = require( './yaxis.js' );


// VARIABLES //

var ELEMENT = 'g';


// RENDER //

/**
* Renders a graph.
*
* @private
* @param {Object} ctx - context
* @returns {VTree} virtual tree
*/
function render( ctx ) {
	var children;
	var props;

	props = {
		'namespace': 'http://www.w3.org/2000/svg',
		'property': 'graph',
		'className': 'graph',
		'attributes': {
			'transform': 'translate('+ctx.state.paddingLeft+','+ctx.state.paddingTop+')'
		}
	};

	children = new Array( 4 );

	debug( 'Rendering background...' );
	children[ 0 ] = bkgd( ctx );

	debug( 'Rendering marks...' );
	children[ 1 ] = marks( ctx );

	debug( 'Rendering x-axis...' );
	children[ 2 ] = xAxis( ctx );

	debug( 'Rendering y-axis...' );
	children[ 3 ] = yAxis( ctx );

	debug( 'Generating a virtual DOM tree (%s) with properties: %s.', ELEMENT, JSON.stringify( props ) );

	return h( ELEMENT, props, children );
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
