'use strict';

// MODULES //

var debug = require( 'debug' )( 'canvas-2d:engine' );
var EventEmitter = require( 'events' ).EventEmitter;
var setReadOnly = require( '@stdlib/utils/define-read-only-property' );
var copy = require( '@stdlib/utils/copy' );
var minstd = require( '@stdlib/math/base/random/minstd' );
var validate = require( './validate.js' );
var defaults = require( './defaults.json' );


// CANVAS ENGINE //

/**
* Canvas engine constructor.
*
* @constructor
* @param {Object} state - engine state
* @param {Options} [options] - engine options
* @throws {TypeError} must provide valid options
* @returns {CanvasEngine} engine instance
*/
function CanvasEngine( state, options ) {
	var self;
	var opts;
	var err;
	if ( !( this instanceof CanvasEngine ) ) {
		if ( arguments.length > 1 ) {
			return new CanvasEngine( state, options );
		}
		return new CanvasEngine( state );
	}
	self = this;
	opts = copy( defaults );
	if ( arguments.length > 1 ) {
		err = validate( opts, options );
		if ( err ) {
			throw err;
		}
	}
	debug( 'Creating an engine with the following configuration: %s.', JSON.stringify( opts ) );
	EventEmitter.call( this );

	// Public, read-only properties...
	setReadOnly( this, 'state', state );

	// "Private" properties...
	Object.defineProperty( this, '_opts', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': opts
	});
	Object.defineProperty( this, '_clipPathID', {
		'configurable': false,
		'enumerable': false,
		'writable': false,
		'value': minstd().toString() // TODO: uuid
	});

	// Bind a listener to update on state changes:
	this.state.on( 'change', onChange );

	return this;

	function onChange() {
		debug( 'Received a change event.' );

		// Check for a change in engine...
		if ( self.state.__engine__ !== self ) {
			debug( 'No longer using engine for rendering. Removing listener...' );
			return self.state.removeListener( 'change', onChange );
		}
		self.render();
	}
} // end FUNCTION CanvasEngine()

/*
* Create a prototype which inherits from the parent prototype.
*/
CanvasEngine.prototype = Object.create( EventEmitter.prototype );

/*
* Set the constructor.
*/
CanvasEngine.prototype.constructor = CanvasEngine;

/**
* SVG transform for translating an x-axis.
*
* @memberof CanvasEngine.prototype
* @type {string}
* @name xAxisTransform
*/
Object.defineProperty( CanvasEngine.prototype, 'xAxisTransform', {
	'configurable': false,
	'enumerable': true,
	'get': require( './props/x-axis-transform/get.js' )
});

/**
* SVG transform for translating a y-axis.
*
* @memberof CanvasEngine.prototype
* @type {string}
* @name yAxisTransform
*/
Object.defineProperty( CanvasEngine.prototype, 'yAxisTransform', {
	'configurable': false,
	'enumerable': true,
	'get': require( './props/y-axis-transform/get.js' )
});

/**
* Renders a plot.
*
* @memberof CanvasEngine.prototype
* @type {Function}
* @name render
* @returns {VTree} virtual tree
*/
setReadOnly( CanvasEngine.prototype, 'render', require( './methods/render.js' ) );


// EXPORTS //

module.exports = CanvasEngine;
