'use strict';

// MODULES //

var debug = require( 'debug' )( 'canvas-2d:engine:render' );
var canvas = require( './../components/canvas.js' );


// RENDER //

/**
* Renders a canvas.
*
* @private
* @returns {VTree} virtual tree
*/
function render() {
	/* jshint validthis: true */ // TODO: eslint
	var vtree;

	debug( 'Rendering canvas...' );

	// Generate the canvas:
	vtree = canvas( this );

	// Announce that a new tree has been rendered:
	this.emit( 'render', vtree );

	return vtree;
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
