'use strict';

// MODULES //

var debug = require( 'debug' )( 'canvas-2d:set:engine' );
var createEngine = require( './../../engines' );
var events = require( './../../events' );
var isValid = require( './../../validators/engine.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'engine' );


// SET //

/**
* Sets the engine.
*
* @private
* @param {string} engine - engine
* @throws {TypeError} must be a string primitive
*/
function set( engine ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( engine );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %s.', this._engine );

	this._engine = engine;
	debug( 'New value: %s.', this._engine );

	// TODO: engine options?
	this.__engine__ = createEngine( this._engine, this );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
