'use strict';

// MODULES //

var debug = require( 'debug' )( 'canvas-2d:set:xtickformat' );
var events = require( './../../events' );
var isValid = require( './../../validators/x_tick_format.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'xTickFormat' );


// SET //

/**
* Sets the x-axis tick format.
*
* @private
* @param {(Function|null)} fmt - axis tick format
* @throws {TypeError} must be a function or null
*/
function set( fmt ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( fmt );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %s.', this._xTickFormat );

	this._xTickFormat = fmt;
	debug( 'New value: %s.', this._xTickFormat );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EXPORTS //

module.exports = set;
