'use strict';

// MODULES //

var Axis = require( '@stdlib/plot/components/svg/axis' );


// GET //

/**
* Returns a y-axis.
*
* @private
* @returns {Axis} axis instance
*/
function get() {
	/* jshint validthis:true */ // TODO: eslint
	var axis = new Axis({
		'scale': this.yScale,
		'label': this.yLabel,
		'tickFormat': this.yTickFormat,
		'numTicks': this.yNumTicks,
		'orientation': this.yAxisOrient,
	});
	return axis;
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
