'use strict';

// MODULES //

var debug = require( 'debug' )( 'canvas-2d:set:ylabel' );
var events = require( './../../events' );
var isValid = require( './../../validators/y_label.js' );


// VARIABLES //

var CHANGE_EVENT = events( 'yLabel' );


// SET //

/**
* Sets the y-axis label.
*
* @private
* @param {string} label - axis label
* @throws {TypeError} must be a string primitive
*/
function set( label ) {
	/*jshint validthis: true */ // TODO: eslint
	var err = isValid( label );
	if ( err ) {
		throw err;
	}
	debug( 'Current value: %s.', this._yLabel );

	this._yLabel = label;
	debug( 'New value: %s.', this._yLabel );

	this.emit( CHANGE_EVENT );
} // end FUNCTION set()


// EYPORTS //

module.exports = set;
