'use strict';

/**
* Returns a label SVG transform.
*
* @private
* @returns {string} SVG transform
*/
function get() {
	/* jshint validthis:true */ // TODO: eslint
	var orient = this.state.orientation;
	if ( orient === 'bottom' || orient === 'top' ) {
		return 'rotate(0)';
	}
	if ( orient === 'left' ) {
		return 'rotate(-90)';
	}
	// right
	return 'rotate(90)';
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
