'use strict';

/**
* Returns the label `y` coordinate.
*
* @private
* @returns {number} `y` coordinate
*/
function get() {
	/* jshint validthis:true */ // TODO: eslint
	var orient = this.state.orientation;
	if ( orient === 'left' ) {
		return -72;
	}
	if ( orient === 'right' ) {
		return 72;
	}
	if ( orient === 'bottom' ) {
		return 45;
	}
	// top
	return -45;
} // end FUNCTION get()


// EXPORTS //

module.exports = get;
