'use strict';

// MODULES //

var debug = require( 'debug' )( 'clippath:engine:render' );
var component = require( './../components' );


// RENDER //

/**
* Renders a virtual DOM tree.
*
* @private
* @returns {VTree} virtual tree
*/
function render() {
	/* jshint validthis: true */ // TODO: eslint
	var vtree;

	debug( 'Rendering a virtual DOM tree...' );

	// Generate the component:
	vtree = component( this );

	// Announce that a new tree has been rendered:
	this.emit( 'render', vtree );

	return vtree;
} // end FUNCTION render()


// EXPORTS //

module.exports = render;
